import React, { useState, useRef } from "react";
import Modal from "../UI/Modal";

import classes from "./ExpenseForm.module.css";

const isEmpty = (value) => value.trim() === "";

const ExpenseForm = (props) => {
  const [enteredName, setenteredName] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [formValidity, setFormValidity] = useState({
    name: true,
    amount: true,
    date: true,
  });

  const enteredNameRef = useRef();
  const enteredAmountRef = useRef();

  const titleChangeHandler = (event) => {
    setenteredName(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.onAddExpense(expenseData);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const enteredNameValue = enteredNameRef.current.value;
    const enteredAmountValue = enteredAmountRef.current.value;
    const enteredDateValue = new Date(enteredDate);

    const enteredNameIsValid = !isEmpty(enteredNameValue);
    const enteredAmountIsValid =
      !isEmpty(enteredAmountValue) && +enteredAmount > 0;
    const enteredDateIsValid = enteredDateValue.toString() !== "Invalid Date";

    setFormValidity({
      name: enteredNameIsValid,
      amount: enteredAmountIsValid,
      date: enteredDateIsValid
    });

    const formIsValid =
      enteredNameIsValid && enteredAmountIsValid && enteredDateIsValid;

    if (!formIsValid) {
      console.log("not valud");
      return;
    }

    const expenseData = {
      title: enteredName,
      amount: +enteredAmount,
      date: new Date(enteredDate),
    };

    saveExpenseDataHandler(expenseData);
    setenteredName("");
    setEnteredAmount("");
    setEnteredDate("");
    props.onCancel();
  };

  return (
    <Modal onCancel={props.onCancel}>
      <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
          <div className={classes.control}>
            <label>Name</label>
            <input
              type="text"
              value={enteredName}
              onChange={titleChangeHandler}
              ref={enteredNameRef}
            />
            {!formValidity.name && <p>Please enter a valid name</p>}
          </div>
          <div className={classes.control}>
            <label>Amount</label>
            <input
              type="number"
              min="0.01"
              step="0.01"
              value={enteredAmount}
              onChange={amountChangeHandler}
              ref={enteredAmountRef}
            />
          </div>
          <div className={classes.control}>
            <label>Date</label>
            <input
              type="date"
              min="2022-01-01"
              max="2024-12-31"
              value={enteredDate}
              onChange={dateChangeHandler}
            />
          </div>
        </div>
        <div className={classes.actions}>
          <button type="button" onClick={props.onCancel}>
            Cancel
          </button>
          <button type="submit">Add Expense</button>
        </div>
      </form>
    </Modal>
  );
};

export default ExpenseForm;
