import React from "react";

import classes from "./ExpensesFilter.module.css";

const ExpensesFilter = (props) => {
  const dropdownChangeHandlerYear = (event) => {
    props.onChangeFilter(event.target.value);
  };

  const dropdownChangeHandlerMonth = (event) => {
    props.onChangeFilter(event.target.value);
  };

  return (
    <div className={classes["expenses-filter"]}>
      <div className={classes["expenses-filter__control"]}>
        <label>Filter by month</label>
        <select value={props.selected} onChange={dropdownChangeHandlerMonth}>
          <option value="Jan">Jan</option>
          <option value="Feb">Feb</option>
          <option value="Mar">Mar</option>
          <option value="Apr">Apr</option>
          <option value="May">May</option>
          <option value="Jun">Jun</option>
          <option value="Jul">Jul</option>
          <option value="Aug">Aug</option>
          <option value="Sep">Sep</option>
          <option value="Oct">Oct</option>
          <option value="Nov">Nov</option>
          <option value="Dec">Dec</option>
        </select>
        <select value={props.selected} onChange={dropdownChangeHandlerYear}>
          <option value="2024">2024</option>
          <option value="2023">2023</option>
          <option value="2022">2022</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
