import { Fragment } from "react";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";
import { Pie } from "react-chartjs-2";

ChartJS.register(ArcElement, Tooltip, Legend);

const PieChart = (props) => {
  let arrLabels = [];
  let arrData = [];
  if(props.data && props.data.length > 0) {
    props.data.map((expense) => {
        arrLabels.push(expense.title);
        console.log(arrLabels);
        return arrLabels;
      });
  }

  return (
    <Fragment>
      <Pie
        width={150}
        height={50}
        data={{
          datasets: [
            {
              labels: arrLabels,
              data: [12, 19, 3, 5, 2, 3],
              backgroundColor: [
                "rgba(255, 99, 132, 0.2)",
                "rgba(54, 162, 235, 0.2)",
                "rgba(255, 206, 86, 0.2)",
                "rgba(75, 192, 192, 0.2)",
                "rgba(153, 102, 255, 0.2)",
                "rgba(255, 159, 64, 0.2)",
              ],
              borderColor: [
                "rgba(255, 99, 132, 1)",
                "rgba(54, 162, 235, 1)",
                "rgba(255, 206, 86, 1)",
                "rgba(75, 192, 192, 1)",
                "rgba(153, 102, 255, 1)",
                "rgba(255, 159, 64, 1)",
              ],
              borderWidth: 1,
            },
          ],
        }}

        options={{
            responsive: true,
            title: {
                text: "chart",
            },
        }}
      />
    </Fragment>
  );
};

export default PieChart;
