import { Fragment } from "react";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";
import { Pie } from "react-chartjs-2";

ChartJS.register(ArcElement, Tooltip, Legend);

const PieChart = (props) => {
  let arrLabels = [];
  let arrData = [];
  let percentData = [];
  if (props.data && props.data.length > 0) {
    arrData.push(props.budget);
    arrLabels.push("Budget");
    props.data.map((expense) => {
      arrLabels.push(expense.title);
      arrData.push(expense.amount);
      let perc = (expense.amount/props.budget) * 100
      props.budget = props.budget - expense.amount;
      percentData.push(perc);
    });
  } else {
    return null;
  }

  return (
    <Fragment>
      <Pie
        data={{
          labels: arrLabels,
          datasets: [
            {
              label: percentData,
              data: arrData,
              backgroundColor: [
                "rgba(255, 99, 132, 0.2)",
                "rgba(54, 162, 235, 0.2)",
                "rgba(255, 206, 86, 0.2)",
                "rgba(75, 192, 192, 0.2)",
                "rgba(153, 102, 255, 0.2)",
                "rgba(255, 159, 64, 0.2)",
              ],
              borderColor: [
                "rgba(255, 99, 132, 1)",
                "rgba(54, 162, 235, 1)",
                "rgba(255, 206, 86, 1)",
                "rgba(75, 192, 192, 1)",
                "rgba(153, 102, 255, 1)",
                "rgba(255, 159, 64, 1)",
              ],
              borderWidth: 1,
            },
          ],
        }}
        options={{
          responsive: true,
          plugins: {
            title: {
              display: true,
              text: "Budget",
            },
          },
        }}
      />
    </Fragment>
  );
};

export default PieChart;
