import { createContext, useReducer, useEffect, useState } from "react";

const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2023, 0, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2023, 2, 28) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2023, 2, 12),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2023, 5, 12),
  },
];

export const BudgetContext = createContext();

const BudgetReducer = (state, action) => {
  switch (action.type) {
    case "ADD_EXPENSE":
      return {
        ...state,
        expenseItems: [...state.expenses, action.payload],
      };
    case "SET_BUDGET":
      return {
        ...state,
        budget: action.payload,
      };
    default:
      return state;
  }
};

export const BudgetProvider = (props) => {
  const [expenseList, setExpenseList] = useState([]);

  useEffect(() => {
    const fetchExpenses = async () => {
      const response = await fetch(
        "https://react-practice-9e728-default-rtdb.firebaseio.com/expenses.json"
      );
      const data = await response.json();

      const loadedExpenses = [];

      for (const key in data) {
        loadedExpenses.push({
          id: key,
          title: data[key].title,
          amount: data[key].amount,
          date: new Date(data[key].date),
        });
      }

      setExpenseList(loadedExpenses);
    };
    fetchExpenses();
  }, []);

  const initialState = {
    budget: 3000,
    remaining: 3000,
    expenses: expenseList,
  };

  const [state, dispatch] = useReducer(BudgetReducer, initialState);
  return (
    <BudgetContext.Provider
      value={{
        budget: state.budget,
        remaining: state.remaining,
        expenseItems: state.expenses,
        dispatch,
      }}
    >
      {props.children}
    </BudgetContext.Provider>
  );
};
