import React, { useContext, useState } from "react";

import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import ExpensesList from "./ExpensesList";
import ExpensesChart from "./ExpensesChart";
import ExpenseForm from "../NewExpense/ExpenseForm";
import NewExpense from "../NewExpense/NewExpense";
import { BudgetContext } from "../context/budget-context";

import classes from "./Expenses.module.css";

const INITIAL_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2022, 1, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2023, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2022, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2023, 5, 12),
  },
];

const Expenses = (props) => {
  const { expense } = useContext(BudgetContext);

  const [expenses, setExpenses] = useState(expense);

  const [formIsShown, setFormIsShown] = useState(false);

  const showFormHandler = () => {
    setFormIsShown(true);
  };

  const hideFormHandler = () => {
    setFormIsShown(false);
  };

  const [filteredMonth, setFilteredMonth] = useState("Jan");

  const filterChangeHandlerMonth = (selectedMonth) => {
    setFilteredMonth(selectedMonth);
  };

  const filteredExpenses = expenses.filter((expense) => {
    const month = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    console.log(month[expense.date.getMonth()] === filteredMonth);
    return month[expense.date.getMonth()] === filteredMonth;
  });

  const addExpenseHandler = (expense) => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };

  const deleteItemHandler = (expenseId) => {
    setExpenses((prevExpense) => {
      const updatedExpenses = prevExpense.filter(
        (expense) => expense.id !== expenseId
      );
      return updatedExpenses;
    });
  };

  return (
    <div>
      {formIsShown && (
        <ExpenseForm
          onAddExpense={addExpenseHandler}
          onCancel={hideFormHandler}
        />
      )}
      <Card className={classes.expenses}>
        <ExpensesFilter
          selectedMonth={filteredMonth}
          onChangeFilterMonth={filterChangeHandlerMonth}
        />
        {false && <ExpensesChart expenses={filteredExpenses} />}
        <ExpensesList
          items={filteredExpenses}
          month={filteredMonth}
          onDeleteItem={deleteItemHandler}
        />
        <NewExpense onShowForm={showFormHandler} onHideForm={hideFormHandler} />
      </Card>
    </div>
  );
};

export default Expenses;
