import classes from "./Total.module.css";

const Total = (props) => {
    const total = props.expenseItems.reduce((total, item) => {
        return (total += item.amount);
    }, 0);

    return <div className={classes.total}>
        <span>Total Spent: ${total.toFixed(2)}</span>
    </div>
};

export default Total;
